(function() {
  window.__loadedAssets__ = [];
  window.__loadAssets__ = function(assets) {
    assets.forEach(function(asset) {
      if (window.__loadedAssets__.includes(asset)) {
        return;
      }

      window.__loadedAssets__.push(asset);
      if (asset.match(/\.js$/)) {
        var script = document.createElement("script");
        script.src = asset;
        script.crossOrigin = 'anonymous';
        document.head.appendChild(script);
      } else if (asset.match(/\.css$/)) {
        var style = document.createElement("link");
        style.rel = 'stylesheet';
        style.type = 'text/css';
        style.href = asset;
        document.head.appendChild(style);
      } else if (asset.match(/\.map$/)) {
      } else {
        console.log('Can not load asset (unknown type): ' + asset)
      }
    });
  }

  var xhr = new XMLHttpRequest();
  xhr.open('GET', 'https://secure.img1-fg.wfcdn.com/webpack/sf-ui-help/bundles/wayfair/en-us/entrypoints.json?cb=' + Math.floor(Date.now() / 1000 / 60));
  xhr.onreadystatechange = function() {
    if(xhr.readyState === 4) {
      if(xhr.status === 200) {
        var entrypoints = JSON.parse(xhr.responseText).entrypoints;
