          var assets = entrypoints[key].assets.map(function(asset) {
            var name = typeof asset === 'string' ? asset : asset.name;
            return "https://secure.img1-fg.wfcdn.com/webpack/sf-ui-help/bundles/wayfair/en-us/" + name;
          });

          window.__loadAssets__(assets);
        });
      } else {
        throw new Error('Received non-OK status when loading entrypoints');
      }
    }
  }

  xhr.send(null);
})();

